targets.query:
SELECT top("value_sum", 10) as "count", "genre" from (
SELECT sum("value") AS "value_sum" FROM "genre_history" WHERE ("user" =~ /^$user$/ AND "service" =~ /^$service$/) AND $timeFilter GROUP BY "genre"
)

SELECT top("minutes", 10) / 1000 / 60 as "minutes", "artists" from (
SELECT sum("duration_ms") AS "minutes" FROM "track_history" WHERE ("user" =~ /^$user$/ AND "service" =~ /^$service$/ AND "artists" =~ /$artist/) AND $timeFilter GROUP BY "artists"
)
